import React from "react";

function Body() {
  return (
    <div className="body">
      <div className="body-content">
        <p>
          {" "}
          Venture across dark, grim lands. Delve into foe-infested dungeons.
          Outwit your opponents and reign champion of Sorrowfen.{" "}
        </p>
        <br />
        <p>
          Little Dungeon Crawlers is an action RPG that takes the fantasy of
          dungeon crawlers and loot grinder games and compacts it both in size
          and in play time into a physical tabletop game.
        </p>
        <br />
        <p className="coming-soon">Coming Soon!</p>
        <p>
          Little Dungeon Crawlers is coming soon! Stay up to date with the
          latest announcements and development & design progress on our socials!
        </p>
      </div>
    </div>
  );
}

export default Body;
